import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles({
    table: {
        minWidth: 600,
    },
});

export default function SbTable(props) {
    console.log(props)
    const classes = useStyles();

    return (
        <div>
            <TableContainer component={Paper}>
                <Table className={classes.table} aria-label="simple table">
                    <TableHead>
                        <TableRow>
                            <TableCell>Code</TableCell>
                            <TableCell>Alarm Price</TableCell>
                            <TableCell>Subscription Date</TableCell>
                            <TableCell>Action</TableCell>
                            <TableCell>SMS Test</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {props.items && props.items.map(subscription => (
                            <TableRow key={subscription._id}>
                                <TableCell>{subscription.code}</TableCell>
                                <TableCell>{subscription.alarmPrice}</TableCell>
                                <TableCell>{subscription.createDate}</TableCell>
                                <TableCell>
                                    <a onClick={() => props.onDelete(props.user._id, subscription._id)}>Delete</a>
                                    {subscription.deleting &&
                                        <img src="data:image/gif;base64," />
                                    }
                                </TableCell>
                                <TableCell><a onClick={() => props.onSendSMS(props.user._id, subscription._id)}>SendSMS</a></TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </div>
    );
}